mapper.insert:

	SELECT PDS_SEQ.NEXTVAL FROM DUAL

INSERT INTO PDS (
    PDS_NO,    BO_NO,    PDS_FILENAME,
    PDS_SAVENAME,    PDS_MIME,    PDS_SIZE,
    PDS_FANCYSIZE
) VALUES (
	#{pds_no},    #{bo_no},    #{pds_filename},
    #{pds_savename},    #{pds_mime},    #{pds_size},
    #{pds_fancysize}
)

INSERT INTO PDS ( PDS_NO, BO_NO, PDS_FILENAME, PDS_SAVENAME, PDS_MIME, PDS_SIZE, PDS_FANCYSIZE ) SELECT PDS_SEQ.NEXTVAL, A.* FROM (
SELECT #{pds.bo_no}, #{pds.pds_filename}, #{pds.pds_savename}, #{pds.pds_mime}, #{pds.pds_size}, #{pds.pds_fancysize} FROM DUAL
) A

mapper.delete:
DELETE FROM PDS
WHERE PDS_NO IN

	#{pds_no}
