mapper.select:
SELECT MEM_ID, MEM_NAME, MEM_ADD1, MEM_ADD2, MEM_HP, MEM_MAIL
FROM CART INNER JOIN MEMBER ON(CART_MEMBER = MEM_ID)
WHERE CART_PROD = #{prod_id}

mapper.update:
UPDATE prod SET PROD_NAME = #{prod_name}, PROD_COST = #{prod_cost}, PROD_PRICE = #{prod_price}, PROD_SALE = #{prod_sale}, PROD_OUTLINE = #{prod_outline}, PROD_DETAIL = #{prod_detail},
PROD_TOTALSTOCK = #{prod_totalstock}, PROD_PROPERSTOCK = #{prod_properstock}, PROD_SIZE = #{prod_size}, PROD_COLOR = #{prod_color}, PROD_DELIVERY = #{prod_delivery}, PROD_UNIT = #{prod_unit}, PROD_QTYIN = #{prod_qtyin}, PROD_QTYSALE = #{prod_qtysale}
,PROD_IMG = #{prod_img}
WHERE PROD_ID = #{prod_id}
